Property.Value:
create PROCEDURE [error].[AddErrorMessage] 
    @schemaName [nvarchar](100) = null,
	@tableName [nvarchar](100) = null,
	@columnName [nvarchar](100) = null,
	@columnId int = null,
	@errorMessage [nvarchar](100) = null
AS

BEGIN TRY
  	BEGIN TRAN upd_error
		UPDATE a
		SET a.[Status] = 'E'
		FROM 
			[recon].[Accounts] a 
		WHERE 
			(
			a.AccountExId IS NULL
			OR 
			a.CountryRegionCode IS NULL 
			OR 
			a.Name IS NULL 
			OR
			a.Type IS NULL 
			)
			AND 
			a.[Status] = 'I'
    COMMIT TRAN upd_error;

	BEGIN TRAN insert_error
	DECLARE @recAccountId int
	DECLARE @columnName nvarchar(100)
	DECLARE cur CURSOR 
	FOR
	SELECT [RecAccountId]
		  ,[ColumnName]
	FROM [error].[ReconAccounts];

		OPEN cur 
		FETCH NEXT FROM cur INTO 
		@recAccountId, 
		@columnName;

		WHILE @@FETCH_STATUS = 0

			BEGIN 
				EXECUTE [error].[AddErrorMessage] 
					@schemaName = 'recon',
					@tableName = 'Accounts', 
					@columnName = @columnName,
					@columnId = @recAccountId,
					@errorMessage = 'Missing attributes'
				FETCH NEXT FROM cur INTO 
					@recAccountId, 
					@columnName;
			END;

		CLOSE cur;
		DEALLOCATE cur;
	COMMIT TRAN insert_error;
END TRY
BEGIN CATCH
	ROLLBACK TRAN upd_error;
	ROLLBACK TRAN insert_error;

	EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Accounts', 
			@columnName = null,
			@columnId = null 
END CATCH

BEGIN TRY
  	BEGIN TRAN upd_error
		UPDATE a
		SET a.[Status] = 'E'
		FROM 
			[recon].[Servers] a 
		WHERE 
			(
			a.CountryRegionCode IS NULL 
			OR 
			a.Name IS NULL 
			OR
			a.Host IS NULL 
			OR
			a.Model IS NULL 
			OR 
			a.SerialNumber IS NULL 
			OR 
			a.WarrantyExpirationDate IS NULL 
			OR 
			a.HardDisk IS NULL 
			OR 
			a.UPS IS NULL 
			OR 
			a.AntivirusSoftware IS NULL 
			)
			AND 
			a.[Status] = 'I'
    COMMIT TRAN upd_error;

	BEGIN TRAN insert_error
		DECLARE @recServerId int
		DECLARE @columnName nvarchar(100)
		DECLARE cur CURSOR 
		FOR
		SELECT 
			s.[RecServerId],
			s.[ColumnName]
		FROM 
			[error].[ReconServers] s;

			OPEN cur 
			FETCH NEXT FROM cur INTO 
			@recServerId, 
			@columnName;

			WHILE @@FETCH_STATUS = 0

				BEGIN 
					EXECUTE [error].[AddErrorMessage] 
						@schemaName = 'recon',
						@tableName = 'Servers', 
						@columnName = @columnName,
						@columnId = @recServerId,
						@errorMessage = 'Missing attributes'
					FETCH NEXT FROM cur INTO 
						@recServerId, 
						@columnName;
				END;

			CLOSE cur;
			DEALLOCATE cur;
	COMMIT TRAN insert_error;
END TRY
BEGIN CATCH
	ROLLBACK TRAN upd_error;
	ROLLBACK TRAN insert_error;

	EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Servers', 
			@columnName = null,
			@columnId = null 
END CATCH

BEGIN TRY
  	BEGIN TRAN upd_error
		UPDATE a
		SET a.[Status] = 'E'
		FROM 
			[recon].[Systems] a 
		WHERE 
			(
			a.CompanyName IS NULL 
			OR 
			a.Name IS NULL 
			OR
			a.Version IS NULL 
			OR 
			a.TechSupportExpDate IS NULL 
			)
			AND 
			a.[Status] = 'I'
    COMMIT TRAN upd_error;

	BEGIN TRAN insert_error
		DECLARE @recSystemId int
		DECLARE @columnName nvarchar(100)
		DECLARE cur CURSOR 
		FOR
		SELECT 
			s.[RecSystemId],
			s.[ColumnName]
		FROM 
			[error].[ReconSystems] s;

			OPEN cur 
			FETCH NEXT FROM cur INTO 
			@recSystemId, 
			@columnName;

			WHILE @@FETCH_STATUS = 0

				BEGIN 
					EXECUTE [error].[AddErrorMessage] 
						@schemaName = 'recon',
						@tableName = 'Systems', 
						@columnName = @columnName,
						@columnId = @recSystemId,
						@errorMessage = 'Missing attributes'
					FETCH NEXT FROM cur INTO 
						@recSystemId, 
						@columnName;
				END;

			CLOSE cur;
			DEALLOCATE cur;
	COMMIT TRAN insert_error;
END TRY
BEGIN CATCH
	ROLLBACK TRAN upd_error;
	ROLLBACK TRAN insert_error;

	EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Systems', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC recon.MergeReconAccounts
END TRY
BEGIN CATCH
		EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Accounts', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC .recon.MergeReconServers
END TRY
BEGIN CATCH
		EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Servers', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC recon.MergeReconSystems
END TRY
BEGIN CATCH
		EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Systems', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC [error].[CheckReconAccounts]
END TRY
BEGIN CATCH
		EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Accounts', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC [GlobalRepository].[error].[CheckReconServers]
END TRY
BEGIN CATCH
		EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Servers', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC [error].[CheckReconSystems]
END TRY
BEGIN CATCH
		EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Systems', 
			@columnName = null,
			@columnId = null 
END CATCH

BEGIN TRY BEGIN TRAN merge_recon MERGE repository.Accounts AS target USING ( SELECT a.[AccountExId] ,cr.CountryId ,u.UserId ,s.SystemId ,s2.ServerId ,a.[Name] ,a.[Description] ,a.[Type] ,a.[PasswordExpires] ,a.RecAccountId FROM [recon].[Accounts] AS a LEFT JOIN repository.CountryRegion cr ON a.CountryRegionCode = cr.CountryRegionCode LEFT JOIN gr_user.Users u ON u.ExternalId = a.UserExId LEFT JOIN repository.Systems s ON s.ExternalId = a.SystemExId LEFT JOIN repository.Servers s2 ON s2.ExternalId = a.ServerExId WHERE a.STATUS = 'I' ) AS source ON source.AccountExId = target.ExternalId WHEN MATCHED THEN UPDATE SET target.CountryId = source.CountryId, target.UserId = source.userid, target.SystemId = source.SystemId, target.ServerId = source.ServerId, target.Name = source.Name, target.Description = source.Description, target.Type = source.Type, target.PasswordExpires = source.PasswordExpires, target.RecAccountId = source.RecAccountId; COMMIT TRAN merge_recon;
BEGIN TRAN upd_recon UPDATE a2 SET a2.STATUS = 'P' FROM [repository].[Accounts] AS a INNER JOIN recon.Accounts a2 ON a2.RecAccountId = a.RecAccountId WHERE a2.STATUS = 'I' COMMIT TRAN upd_recon; END TRY BEGIN CATCH
ROLLBACK TRAN merge_recon;
ROLLBACK TRAN upd_recon;
EXECUTE [error].[AddErrorMessage] @schemaName = 'recon', @tableName = 'Accounts', @columnName = null, @columnId = null END CATCH

BEGIN TRY BEGIN TRAN merge_recon MERGE repository.Servers AS target USING ( SELECT s.[ServerExId] ,s.[Name] ,s.[Host] ,cr.[CountryId] ,s.[Model] ,s.[SerialNumber] ,s.[WarrantyExpirationDate] ,s.[CPUType] ,s.[RAM] ,s.[HardDisk] ,s.[UPS] ,s.[AntivirusSoftware] ,s.[RecServerId] FROM [recon].[Servers] AS s LEFT JOIN repository.CountryRegion cr ON s.CountryRegionCode = cr.CountryRegionCode WHERE s.STATUS = 'I' ) AS source ON source.[ServerExId] = target.[ExternalId] WHEN MATCHED THEN UPDATE SET target.[Name] = source.[Name], target.[Host] = source.[Host], target.[CountryId] = source.[CountryId], target.[Model] = source.[Model], target.[SerialNumber] = source.[SerialNumber], target.[WarrantyExpirationDate] = source.[WarrantyExpirationDate], target.[CPUType] = source.[CPUType], target.[RAM] = source.[RAM], target.[HardDisk] = source.[HardDisk], target.[UPS] = source.[UPS], target.[AntivirusSoftware] = source.[AntivirusSoftware], target.[RecServerId] = source.[RecServerId]; COMMIT TRAN merge_recon;
BEGIN TRAN upd_recon UPDATE a2 SET a2.STATUS = 'P' FROM [repository].[Servers] AS a INNER JOIN [recon].[Servers] a2 ON a2.[ServerExId] = a.[ExternalId] WHERE a2.STATUS = 'I' COMMIT TRAN upd_recon; END TRY BEGIN CATCH
ROLLBACK TRAN merge_recon;
ROLLBACK TRAN upd_recon;
EXECUTE [error].[AddErrorMessage] @schemaName = 'recon', @tableName = 'Servers', @columnName = null, @columnId = null END CATCH

BEGIN TRY BEGIN TRAN merge_recon MERGE repository.Systems AS target USING ( SELECT s.[SystemExId] ,s.[CompanyName] ,s.[Name] ,s.[Version] ,s.[TechSupportExpDate] ,s.[RecSystemId] FROM [recon].[Systems] AS s WHERE s.STATUS = 'I' ) AS source ON source.[SystemExId] = target.[ExternalId] WHEN MATCHED THEN UPDATE SET target.[CompanyName] = source.[CompanyName], target.[Name] = source.[Name], target.[Version] = source.[Version], target.[TechSupportExpDate] = source.[TechSupportExpDate], target.[RecSystemId] = source.[RecSystemId]; COMMIT TRAN merge_recon;
BEGIN TRAN upd_recon UPDATE a2 SET a2.STATUS = 'P' FROM [repository].[Systems] AS a INNER JOIN [recon].[Systems] a2 ON a2.[SystemExId] = a.[ExternalId] WHERE a2.STATUS = 'I' COMMIT TRAN upd_recon; END TRY BEGIN CATCH
ROLLBACK TRAN merge_recon;
ROLLBACK TRAN upd_recon;
EXECUTE [error].[AddErrorMessage] @schemaName = 'recon', @tableName = 'Systems', @columnName = null, @columnId = null END CATCH

CREATE FUNCTION repository.CheckDate 
(
	@Param1 datetime
)
RETURNS int
AS

BEGIN TRY
	EXEC sp_MSforeachtable "ALTER TABLE ? NOCHECK CONSTRAINT all"
END TRY
BEGIN CATCH
	EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'service',
			@tableName = null, 
			@columnName = null,
			@columnId = null 
END CATCH

BEGIN TRY
	EXEC sp_MSforeachtable @command1="print '?'", @command2="ALTER TABLE ? WITH CHECK CHECK CONSTRAINT all"
END TRY
BEGIN CATCH
	EXECUTE [error].[AddErrorMessage] 
			@schemaName = 'service',
			@tableName = null, 
			@columnName = null,
			@columnId = null 
END CATCH
