create PROCEDURE [error].[AddErrorMessage] 
    @schemaName [nvarchar](100) = null,
	@tableName [nvarchar](100) = null,
	@columnName [nvarchar](100) = null,
	@columnId int = null,
	@errorMessage [nvarchar](100) = null
AS

BEGIN TRY
  	BEGIN TRAN upd_error
		UPDATE a
		SET a.[Status] = 'E'
		FROM 
			[GlobalRepository].[recon].[Accounts] a 
		WHERE 
			(
			a.AccountExId IS NULL
			OR 
			a.CountryRegionCode IS NULL 
			OR 
			a.Name IS NULL 
			OR
			a.Type IS NULL 
			)
			AND 
			a.[Status] = 'I'
    COMMIT TRAN upd_error;

	BEGIN TRAN insert_error
	DECLARE @recAccountId int
	DECLARE @columnName nvarchar(100)
	DECLARE cur CURSOR 
	FOR
	SELECT [RecAccountId]
		  ,[ColumnName]
	FROM [GlobalRepository].[error].[ReconAccounts];

		OPEN cur 
		FETCH NEXT FROM cur INTO 
		@recAccountId, 
		@columnName;

		WHILE @@FETCH_STATUS = 0

			BEGIN 
				EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
					@schemaName = 'recon',
					@tableName = 'Accounts', 
					@columnName = @columnName,
					@columnId = @recAccountId,
					@errorMessage = 'Missing attributes'
				FETCH NEXT FROM cur INTO 
					@recAccountId, 
					@columnName;
			END;

		CLOSE cur;
		DEALLOCATE cur;
	COMMIT TRAN insert_error;
END TRY
BEGIN CATCH
	ROLLBACK TRAN upd_error;
	ROLLBACK TRAN insert_error;

	EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Accounts', 
			@columnName = null,
			@columnId = null 
END CATCH

BEGIN TRY
  	BEGIN TRAN upd_error
		UPDATE a
		SET a.[Status] = 'E'
		FROM 
			[GlobalRepository].[recon].[Servers] a 
		WHERE 
			(
			a.CountryRegionCode IS NULL 
			OR 
			a.Name IS NULL 
			OR
			a.Host IS NULL 
			OR
			a.Model IS NULL 
			OR 
			a.SerialNumber IS NULL 
			OR 
			a.TechSupport IS NULL 
			OR 
			a.WarrantyExpirationDate IS NULL 
			OR 
			a.HardDisk IS NULL 
			OR 
			a.UPS IS NULL 
			OR 
			a.AntivirusSoftware IS NULL 
			)
			AND 
			a.[Status] = 'I'
    COMMIT TRAN upd_error;

	BEGIN TRAN insert_error
		DECLARE @recServerId int
		DECLARE @columnName nvarchar(100)
		DECLARE cur CURSOR 
		FOR
		SELECT 
			s.[RecServerId],
			s.[ColumnName]
		FROM 
			[GlobalRepository].[error].[ReconServers] s;

			OPEN cur 
			FETCH NEXT FROM cur INTO 
			@recServerId, 
			@columnName;

			WHILE @@FETCH_STATUS = 0

				BEGIN 
					EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
						@schemaName = 'recon',
						@tableName = 'Servers', 
						@columnName = @columnName,
						@columnId = @recServerId,
						@errorMessage = 'Missing attributes'
					FETCH NEXT FROM cur INTO 
						@recServerId, 
						@columnName;
				END;

			CLOSE cur;
			DEALLOCATE cur;
	COMMIT TRAN insert_error;
END TRY
BEGIN CATCH
	ROLLBACK TRAN upd_error;
	ROLLBACK TRAN insert_error;

	EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Servers', 
			@columnName = null,
			@columnId = null 
END CATCH

BEGIN TRY
  	BEGIN TRAN upd_error
		UPDATE a
		SET a.[Status] = 'E'
		FROM 
			[GlobalRepository].[recon].[Systems] a 
		WHERE 
			(
			a.CompanyName IS NULL 
			OR 
			a.Name IS NULL 
			OR
			a.Version IS NULL 
			OR
			a.TechSupport IS NULL 
			OR 
			a.TechSupportExpDate IS NULL 
			)
			AND 
			a.[Status] = 'I'
    COMMIT TRAN upd_error;

	BEGIN TRAN insert_error
		DECLARE @recSystemId int
		DECLARE @columnName nvarchar(100)
		DECLARE cur CURSOR 
		FOR
		SELECT 
			s.[RecSystemId],
			s.[ColumnName]
		FROM 
			[GlobalRepository].[error].[ReconSystems] s;

			OPEN cur 
			FETCH NEXT FROM cur INTO 
			@recSystemId, 
			@columnName;

			WHILE @@FETCH_STATUS = 0

				BEGIN 
					EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
						@schemaName = 'recon',
						@tableName = 'Systems', 
						@columnName = @columnName,
						@columnId = @recSystemId,
						@errorMessage = 'Missing attributes'
					FETCH NEXT FROM cur INTO 
						@recSystemId, 
						@columnName;
				END;

			CLOSE cur;
			DEALLOCATE cur;
	COMMIT TRAN insert_error;
END TRY
BEGIN CATCH
	ROLLBACK TRAN upd_error;
	ROLLBACK TRAN insert_error;

	EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Systems', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC [GlobalRepository].recon.MergeReconAccounts
END TRY
BEGIN CATCH
		EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Accounts', 
			@columnName = null,
			@columnId = null 
END CATCH


