END CATCH


BEGIN TRY
	EXEC [GlobalRepository].recon.MergeReconSystems
END TRY
BEGIN CATCH
		EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Systems', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC [GlobalRepository].[error].[CheckReconAccounts]
END TRY
BEGIN CATCH
		EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Accounts', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC [GlobalRepository].[error].[CheckReconServers]
END TRY
BEGIN CATCH
		EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Servers', 
			@columnName = null,
			@columnId = null 
END CATCH


BEGIN TRY
	EXEC [GlobalRepository].[error].[CheckReconSystems]
END TRY
BEGIN CATCH
		EXECUTE [GlobalRepository].[error].[AddErrorMessage] 
			@schemaName = 'recon',
			@tableName = 'Systems', 
			@columnName = null,
			@columnId = null 
END CATCH

BEGIN TRY BEGIN TRAN merge_recon MERGE repository.Accounts AS target USING ( SELECT a.[AccountExId] ,cr.CountryId ,u.UserId ,s.SystemId ,s2.ServerId ,a.[Name] ,a.[Description] ,a.[Type] ,a.[PasswordExpires] ,a.RecAccountId FROM [GlobalRepository].[recon].[Accounts] AS a LEFT JOIN repository.CountryRegion cr ON a.CountryRegionCode = cr.CountryRegionCode LEFT JOIN gr_user.Users u ON u.ExternalId = a.UserExId LEFT JOIN repository.Systems s ON s.ExternalId = a.SystemExId LEFT JOIN repository.Servers s2 ON s2.ExternalId = a.ServerExId WHERE a.STATUS = 'I' ) AS source ON source.AccountExId = target.ExternalId WHEN MATCHED THEN UPDATE SET target.CountryId = source.CountryId, target.UserId = source.userid, target.SystemId = source.SystemId, target.ServerId = source.ServerId, target.Name = source.Name, target.Description = source.Description, target.Type = source.Type, target.PasswordExpires = source.PasswordExpires, target.RecAccountId = source.RecAccountId; COMMIT TRAN merge_recon;
BEGIN TRAN upd_recon UPDATE a2 SET a2.STATUS = 'P' FROM [repository].[Accounts] AS a INNER JOIN recon.Accounts a2 ON a2.RecAccountId = a.RecAccountId WHERE a2.STATUS = 'I' COMMIT TRAN upd_recon; END TRY BEGIN CATCH
ROLLBACK TRAN merge_recon;
ROLLBACK TRAN upd_recon;
EXECUTE [GlobalRepository].[error].[AddErrorMessage] @schemaName = 'recon', @tableName = 'Accounts', @columnName = null, @columnId = null END CATCH

BEGIN TRY BEGIN TRAN merge_recon MERGE repository.Servers AS target USING ( SELECT s.[ServerExId] ,s.[Name] ,s.[Host] ,cr.[CountryId] ,s.[Model] ,s.[SerialNumber] ,s.[TechSupport] ,s.[WarrantyExpirationDate] ,s.[CPUType] ,s.[RAM] ,s.[HardDisk] ,s.[UPS] ,s.[AntivirusSoftware] FROM [GlobalRepository].[recon].[Servers] AS s LEFT JOIN repository.CountryRegion cr ON s.CountryRegionCode = cr.CountryRegionCode WHERE s.STATUS = 'I' ) AS source ON source.[ServerExId] = target.[ExternalId] WHEN MATCHED THEN UPDATE SET target.[Name] = source.[Name], target.[Host] = source.[Host], target.[CountryId] = source.[CountryId], target.[Model] = source.[Model], target.[SerialNumber] = source.[SerialNumber], target.[TechSupport] = source.[TechSupport], target.[WarrantyExpirationDate] = source.[WarrantyExpirationDate], target.[CPUType] = source.[CPUType], target.[RAM] = source.[RAM], target.[HardDisk] = source.[HardDisk], target.[UPS] = source.[UPS], target.[AntivirusSoftware] = source.[AntivirusSoftware]; COMMIT TRAN merge_recon;
BEGIN TRAN upd_recon UPDATE a2 SET a2.STATUS = 'P' FROM [repository].[Servers] AS a INNER JOIN [recon].[Servers] a2 ON a2.[ServerExId] = a.[ExternalId] WHERE a2.STATUS = 'I' COMMIT TRAN upd_recon; END TRY BEGIN CATCH
ROLLBACK TRAN merge_recon;
ROLLBACK TRAN upd_recon;
EXECUTE [GlobalRepository].[error].[AddErrorMessage] @schemaName = 'recon', @tableName = 'Servers', @columnName = null, @columnId = null END CATCH

BEGIN TRY BEGIN TRAN merge_recon MERGE repository.Systems AS target USING ( SELECT s.[SystemExId] ,s.[CompanyName] ,s.[Name] ,s.[Version] ,s.[TechSupport] ,s.[TechSupportExpDate] FROM [GlobalRepository].[recon].[Systems] AS s WHERE s.STATUS = 'I' ) AS source ON source.[SystemExId] = target.[ExternalId] WHEN MATCHED THEN UPDATE SET target.[CompanyName] = source.[CompanyName], target.[Name] = source.[Name], target.[Version] = source.[Version], target.[TechSupport] = source.[TechSupport], target.[TechSupportExpDate] = source.[TechSupportExpDate]; COMMIT TRAN merge_recon;
BEGIN TRAN upd_recon UPDATE a2 SET a2.STATUS = 'P' FROM [repository].[Systems] AS a INNER JOIN [recon].[Systems] a2 ON a2.[SystemExId] = a.[ExternalId] WHERE a2.STATUS = 'I' COMMIT TRAN upd_recon; END TRY BEGIN CATCH
ROLLBACK TRAN merge_recon;
ROLLBACK TRAN upd_recon;
EXECUTE [GlobalRepository].[error].[AddErrorMessage] @schemaName = 'recon', @tableName = 'Systems', @columnName = null, @columnId = null END CATCH


BEGIN
SET NOCOUNT ON;

BEGIN TRAN aud

DECLARE 
	@DateFrom datetime,
	@ExternalId uniqueidentifier,
	@AccountId int,
	@NEW_CountryId int,
	@OLD_CountryId int,
	@NEW_UserId int,
	@OLD_UserId int,
	@NEW_SystemId int,
	@OLD_SystemId int,
	@NEW_ServerId int,
	@OLD_ServerId int,
	@NEW_Name nvarchar(50),
	@OLD_Name nvarchar(50),
	@NEW_Description nvarchar(200),
	@OLD_Description nvarchar(200),
	@NEW_Type char(1),
	@OLD_Type char(1),
	@NEW_PasswordExpires datetime,
	@OLD_PasswordExpires datetime,
	@NEW_Tofix char(1),
	@OLD_Tofix char(1),
	@NEW_RecAccountId int,
	@OLD_RecAccountId int;

	SELECT 
		@DateFrom = isnull(a.EditDate,a.CreationDate),
		@ExternalId =  a.ExternalId,
		@AccountId = a.AccountId,
		@NEW_CountryId = a.CountryId,
		@OLD_CountryId = d.CountryId,
		@NEW_UserId = a.UserId,
		@OLD_UserId = d.UserId,
		@NEW_SystemId = a.SystemId,
		@OLD_SystemId = d.SystemId,
		@NEW_ServerId = a.ServerId,
		@OLD_ServerId = d.ServerId,
		@NEW_Name  = a.Name,
		@OLD_Name  = d.Name,
		@NEW_Description  = a.Description,
		@OLD_Description  = d.Description,
		@NEW_Type = a.Type,
		@OLD_Type = d.Type,
		@NEW_PasswordExpires  = a.PasswordExpires,
		@OLD_PasswordExpires  = d.PasswordExpires,
		@NEW_Tofix  = a.Tofix,
		@OLD_Tofix  = d.Tofix,
		@NEW_RecAccountId = a.RecAccountId,
		@OLD_RecAccountId = d.RecAccountId
	FROM 
		[repository].Accounts a 
		JOIN  deleted d ON d.AccountId = a.AccountId

   INSERT INTO [GlobalRepository].[audit].[Accounts]
   (   
      [DateFrom],
      [ExternalId],
      [AccountId],
      [NEW_CountryId],
      [OLD_CountryId],
      [NEW_UserId],
      [OLD_UserId],
      [NEW_SystemId],
      [OLD_SystemId],
      [NEW_ServerId],
      [OLD_ServerId],
      [NEW_Name],
      [OLD_Name],
      [NEW_Description],
      [OLD_Description],
      [NEW_Type],
      [OLD_Type],
      [NEW_PasswordExpires],
      [OLD_PasswordExpires],
      [NEW_Tofix],
      [OLD_Tofix],
      [NEW_RecAccountId],
      [OLD_RecAccountId]
	)
	VALUES 
	(
		@DateFrom,
		@ExternalId,
		@AccountId,
		@NEW_CountryId,
		@OLD_CountryId,
		@NEW_UserId,
		@OLD_UserId,
		@NEW_SystemId,
		@OLD_SystemId,
		@NEW_ServerId,
		@OLD_ServerId,
		@NEW_Name,
		@OLD_Name,
		@NEW_Description,
		@OLD_Description,
		@NEW_Type,
		@OLD_Type,
		@NEW_PasswordExpires,
		@OLD_PasswordExpires,
		@NEW_Tofix,
		@OLD_Tofix,
		@NEW_RecAccountId,
		@OLD_RecAccountId  
	)
COMMIT TRAN aud

BEGIN TRAN upd
    UPDATE a
	SET a.EditDate = getdate()
	FROM 
		[repository].Accounts a 
		JOIN inserted i ON i.AccountId = a.AccountId
COMMIT TRAN upd
END

